$buttonList = array();        if ($saveData[id]) {            $buttonList[save][name] = "Ort speichern";        } else {            $buttonList[save][name] = "Ort anlegen";        }                // Zeige Datensatz        if ($saveData[id]>0) {            $addData = array();            $addData[type] = "link";            $addData["class"] = 'cmsLinkButton cmsSecond';            $addData[link] = "adressen.php?location=$saveData[id]";            $addData[name] = "Ort Zeigen";            $buttonList[show] = $addData;        }        return $buttonList;    }    }?>