<?php // charset:UTF-8class cmsType_header extends cmsType_header_base {    function headerShow ($contentData,$frameWidth) {        $data = $contentData[data];        if (!is_array($data)) $data = array();        $height = $data[height];        if (!$height) $height = 80;        $background = $data[background];        if (!$background) $background="#cfc";        $border = 0;        $innerWidth = $frameWidth;// - 10 - 2*$border;        echo ("<div class='header'>");        echo ("<div class='maincontent'>");        echo ("<div class='header_logo'>");        echo ("<a href='index.php'><img src='img/logo.jpg' alt='KlappeAuf Karlsruhe' class='noborder' /></a>");        echo ("</div>");                                              $root = $_SERVER[DOCUMENT_ROOT];        $cmsName = $GLOBALS[cmsName];        if (file_exists($root."/".$cmsName)) $root .= "/$cmsName";                if (file_exists($root."/incs/header-mainmenu.php")) {            echo("<div class='main_menu' id='mainmenucontainer'>\n");            echo("<div id='mainmenu'>");            include($root."/incs/header-mainmenu.php");            echo("</div>");            echo("</div>");            echo("<div style='clear:both;'></div>");            echo("</div>");        } else {            echo ("Not exist ".$root." /klappeAuf/incs/header-mainmenu.php<br>");        }                                       return 0;                                div_start("header","width:".$innerWidth."px;");        div_start("headerLeft","float:left;");        echo("<a href='index.php' class='headerStartLink' >");        echo ($this->headerLogo());        echo("</a><br>\n");        span_text($this->headerSlogan(),array("class"=>"headerSlogan","width"=>"auto"));        div_end("headerLeft");        div_start("headerRight","float:left;");        // span_text($this->headerName(),array("width"=>"auto","text-align"=>"right","class"=>"headerName"));        //$this->headerName();        // echo ("<br>");                //echo ($this->headerSlogan());        div_end("headerRight");        div_end("header","before");    }    function headerLogo() {        global $cmsName;        $homePath = "";        if (file_exists($_SERVER['DOCUMENT_ROOT']."/".$cmsName."/")) $homePath = "/".$cmsName."/";                return "<img class='headerLogo' src='".$homePath."style/logo.png' title='klappeAuf' name='klappeAuf' alt='klappeAuf' >";    }    function headerName() {        return "KLAPPEauf";    }    function headerSlogan () {        return "Das Kulturmagazin der Region Karlsruhe"; // Â·??    }}?>