<?php // charset:UTF-8class cmsType_contentTypes extends  cmsType_contentTypes_base {    function useType($type) {        switch ($type) {            case "companyList" : $useType = 1; break;            case "empty" : $useType = 0; break;            case "content" : $useType = 0; break;            case "basket" : $useType = 0; break;                            default :                $useType = 1;        }        return $useType;    }            function use_special_viewFilter($editContent,$dataName="editContent") {        $data = $editContent[data];        if (!is_array($data)) $data = array();                        $res = array();        $addData = array();        $addData["text"] = "Projekt gesetzt";        if ($data[projectSet]) $checked = "checked='checked'";        else $checked = "";        $addData["input"] = "<input type='checkBox' $checked name='".$dataName."[data][projectSet]' value='1'>";        $res["project"] = $addData;                $addData = array();        $addData["text"] = "Bohrer gesetzt";        if ($data[drillSet]) $checked = "checked='checked'";        else $checked = "";        $addData["input"] = "<input type='checkBox' $checked name='".$dataName."[data][drillSet]' value='1' >";        $res["drill"] = $addData;                                return $res;           }    }?>