<?php // charset:UTF-8class cmsType_header extends cmsType_header_base {    function headerSpecial($contentData,$textData) {        $str = "";        $project = $_SESSION[project];        if ($project) {            $project = intval($project);            $projectData = cmsCategory_getById($project);            if (is_array($projectData)) {                                                $imageId = $projectData[image];                if ($imageId) {                    $imageWidth = 100;                    $imageHeight = 60;                    $showData = array();                    $showData[hAlign] = "left";                    $showData[vAlign] = "top";                    $showData[frameWidth] = $imageWidth;                    $showData[frameHeight] = $imageHeight;                    // $showData[ratio] = 30 / 5;                                            $imageData = cmsImage_getData_by_Id($imageId);                    if (is_array($imageData)) {                        $imgStr = cmsImage_showImage($imageData, $imageWidth,$showData);                        $str .= "<div class='headerVmtData' style='width:300px'>";                        $str .= "<div class='headerVmtProject' style='width:".$imageWidth."px;float:left;'>";                        $str .= "<a href='projectChoice.php' >$imgStr</a>";                                                $str .= "</div>";                        $str .= "<div class='headerVmtDataText' style='width:".(300-$imageWidth-10)."px;margin-left:10px;float:left;'>";                    }                }                                $title = $projectData[name];                $str .= "Projekt: <b>$title</b><br>";                                // $str .= "Image = $imageId <br>";                                $drill = $_SESSION[drill];                if ($drill) {                    $drillId = intval($drill);                    $drillData = cmsCategory_getById($drillId);                    if (is_array($drillData)) {                        $title = $drillData[name];                        $str .= "Bohrer: <b>$title</b><br>";                    }                } else {                    $str .= "Kein Bohrer gewählt";                }                                if (is_array($imageData)) {                    $str .= "</div>";                    $str .= "<div style='clear:both;' ></div>";                    $str .= "</div>";                }                            }                                            } else {            $str = "Kein Projekt gewählt";        }        return $str;    }}?>